#include <stdio.h>

#define SIZE 8

void sort(int array[], int size)
{
    for(int i = 0; i < size; i++)
    {
        int smallest = i;
        for(int j = i; j < size; j++)
        {
            if(array[j] < array[smallest])
            {
                smallest = j;
            }
        }
        int temp = array[i];
        array[i] = array[smallest];
        array[smallest] = temp;
    }
}

